import React, { Component } from "react";
import CardList from "../Components/CardList";
import SearchBox from "../Components/SearchBox";
import "./App.css";
import Scroll from "../Components/Scroll";
import ErrorBoundry from "../Components/ErrorBoundry";

class App extends Component {
  constructor() {
    super();
    this.state = {
      robots: [],
      searchfield: "",
    };
  }

  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((response) => response.json())
      .then((users) => this.setState({ robots: users }));
  }

  onSearchChange = (event) => {
    this.setState({ searchfield: event.target.value });
  };

  render() {
    const { robots, searchfield } = this.state;
    const filteredRobots = robots.filter((robot) => {
      return robot.username.toLowerCase().includes(searchfield.toLowerCase());
    });
    return !robots.length ? (
      <h1 className="tc f1">Loading...</h1>
    ) : (
      <div className="tc min-vh-100">
        <div className="flex flex-wrap justify-center items-center">
          <h1 className="f1 mh5">AI HELPERS</h1>
          <SearchBox searchChange={this.onSearchChange} />
        </div>
        <Scroll>
          <ErrorBoundry>
            <CardList robots={filteredRobots} />
          </ErrorBoundry>
        </Scroll>
        <div className="bt b--near-white mh5 mv5 fixed top-0">
            <h1 className="f2 mh5 mv5 white">AI HELPERS</h1>
        </div>
      </div>
    );
  }
}

export default App;
